import { Component, OnInit } from '@angular/core'
import { UsersService } from '../../services/users.service'
import { User } from '../../models/Users'
@Component({
    selector: 'app-buscar-us',
    templateUrl: './buscar-us.component.html',
    styleUrls: ['./buscar-us.component.css'],
})
export class BuscarUsComponent implements OnInit {
    constructor(private userService: UsersService) {}
    nombre = ''
    // dataUsers: User[] = []
    ngOnInit(): void {}
    obsdata() {
        // this.dataUsers = []
        this.userService.dataUser(this.nombre).subscribe(
            // async (res: any) => {
            //     res.forEach((datos: User) => {
            //         this.dataUsers.push(datos)
            //     })
            // },
            (res) => console.log(res),
            (err) => console.log(err),
        )
    }
}
<!DOCTYPE html>
<html lang="en">
    <head>
        <meta charset="UTF-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Document</title>
    </head>
    <body>
        <app-nav></app-nav>
        <form class="d-flex" role="search">
            <input
                class="form-control me-2"
                type="search"
                [(ngModel)]="nombre"
                placeholder="Search"
                aria-label="Search"
            />
            <button
                class="btn btn-outline-success"
                type="submit"
                (onclick)="obsdata()"
            >
                Search
            </button>
        </form>
    </body>
</html>
