import { Component, OnInit } from '@angular/core'
import { UsersService } from '../../services/users.service'
import { User } from '../../models/Users'
@Component({
    selector: 'app-buscar-us',
    templateUrl: './buscar-us.component.html',
    styleUrls: ['./buscar-us.component.css'],
})
export class BuscarUsComponent implements OnInit {
    constructor(private userService: UsersService) {}
    nombre = ''
    dataUsers: any = []
    ngOnInit(): void {}
    obsdata() {
        this.dataUsers = []
        this.userService.dataUser(this.nombre).subscribe(
            async (res: any) => {
                res.forEach((datos: any) => {
                    this.dataUsers.push(datos)
                })
            },
            (err) => console.log(err),
        )
    }
}
<app-nav></app-nav>
<form class="d-flex" role="search">
    <input
        class="form-control me-2"
        type="search"
        [(ngModel)]="nombre"
        placeholder="Search"
        aria-label="Search"
    />
    <button class="btn btn-outline-success" type="submit" (onclick)="obsdata()">
        Search
    </button>
    <div class="card" style="width: 18rem">
        <ul class="list-group list-group-flush">
            <li class="list-group-item">
                <label>NOMBRE: </label> {{ user.nombre }}
            </li>
            <li class="list-group-item">
                <label>APELLIDO: </label>{{ user.apellido }}
            </li>
            <li class="list-group-item">
                <label>CARNE: </label> {{ user.carne }}
            </li>
        </ul>
    </div>
</form>
